ursavgiften.', 'Sponsring 2020', '', 'inherit', 'closed', 'closed', '', '237-revision-v1', '', '', '2018-10-16 10:58:25', '2018-10-16 08:58:25', '', 237, 'http://tangobrandalliance.se/237-revision-v1/', 0, 'revision', '', 0),
